// src/GameRoom.js

import React, { useState, useEffect } from 'react';

// Suponha que gameId e playerId s√£o passados como props
function GameRoom({ gameId, playerId }) {
  const [gameState, setGameState] = useState(null);
  const [socket, setSocket] = useState(null);

  // useEffect para conectar ao WebSocket quando o componente for montado
  useEffect(() => {
    // Cria a URL do WebSocket a partir das props
    const wsUrl = `ws://127.0.0.1:8001/ws/${gameId}/${playerId}`;
    const ws = new WebSocket(wsUrl);

    ws.onopen = () => {
      console.log('Conectado ao servidor WebSocket!');
      setSocket(ws);
    };

    // Onde a m√°gica acontece: escuta as mensagens do backend
    ws.onmessage = (event) => {
      const message = JSON.parse(event.data);
      console.log('Mensagem recebida:', message);

      // Atualiza o estado do nosso componente com os dados do jogo
      if (message.payload) {
        setGameState(message.payload);
      }
    };

    ws.onclose = () => {
      console.log('Desconectado do servidor WebSocket.');
    };

    // Fun√ß√£o de limpeza: fecha a conex√£o quando o componente √© desmontado
    return () => {
      ws.close();
    };
  }, [gameId, playerId]); // Roda este efeito apenas se gameId ou playerId mudarem

  // Fun√ß√£o para enviar a escolha do jogador para o backend
  const handlePlayerChoice = (choice) => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send(JSON.stringify({
        action: 'CHOOSE',
        choice: choice, // "COOPERAR" ou "TRAIR"
      }));
    }
  };

  // Renderiza√ß√£o b√°sica (pode ser muito melhorada!)
  if (!gameState) {
    return <div>Carregando sala de jogo...</div>;
  }

  const amIPlayer = gameState.players[playerId];

  return (
    <div>
      <h1>Jogo: {gameState.game_id}</h1>
      <h2>Rodada: {gameState.current_round}</h2>
      <h3>Ol√°, {amIPlayer?.name}! Sua pontua√ß√£o: {amIPlayer?.score}</h3>

      <div>
        <h3>Jogadores na Sala:</h3>
        <ul>
          {Object.values(gameState.players).map(p => (
            <li key={p.id}>{p.name} - {p.score} pontos</li>
          ))}
        </ul>
      </div>

      {/* Bot√µes de A√ß√£o */}
      <div style={{ marginTop: '20px' }}>
        <button onClick={() => handlePlayerChoice('COOPERAR')} style={{ marginRight: '10px' }}>
          ü§ù Cooperar
        </button>
        <button onClick={() => handlePlayerChoice('TRAIR')}>
          üî™ Trair
        </button>
      </div>
    </div>
  );
}

export default GameRoom;
