// src/App.js
import React, { useState } from 'react';
import Lobby from './Lobby';
import GameRoom from './GameRoom'; // Vamos melhorar este arquivo a seguir
import './App.css'; // Vamos criar este arquivo para estilos básicos

function App() {
  const [gameInfo, setGameInfo] = useState(null);

  // Esta função será chamada pelo Lobby quando o jogador entrar em um jogo
  const handleGameJoined = (info) => {
    setGameInfo(info);
  };

  return (
    <div className="App">
      {!gameInfo ? (
        <Lobby onGameJoined={handleGameJoined} />
      ) : (
        <GameRoom
          gameId={gameInfo.gameId}
          playerId={gameInfo.playerId}
          isHost={gameInfo.isHost}
        />
      )}
    </div>
  );
}

export default App;
