// src/App.js
import React, { useState } from 'react';
import Lobby from './Lobby';
import GameRoom from './GameRoom';
import './App.css';

function App() {
  const [gameInfo, setGameInfo] = useState(null);

  const handleGameJoined = (info) => {
    // LOG 1: Vamos ver se esta função está sendo chamada
    console.log('App.js: Jogo ingressado com a informação:', info);
    setGameInfo(info);
  };

  // LOG 2: Vamos ver o que o App está renderizando
  console.log('App.js: Renderizando com gameInfo:', gameInfo);

  return (
    <div className="App">
      {!gameInfo ? (
        <Lobby onGameJoined={handleGameJoined} />
      ) : (
        <GameRoom
          gameId={gameInfo.gameId}
          playerId={gameInfo.playerId}
          isHost={gameInfo.isHost}
          hostId={gameInfo.hostId}
        />
      )}
    </div>
  );
}

export default App;
